import React, { useEffect, useState } from 'react'
import { Link } from "react-router-dom"
import axios from "axios"

const AllStore = () => {
    const [store,setStore]= useState([])
    const navigate = useNavigate()
    const [errors,setErrors]=useState(null)
    useEffect(() => {
      axios.get("http://localhost:5000/api/Store")
          .then(res => {
              console.log(res.data)
              setStore(res.data)
          
           
          })
          .catch(err => {
              console.log(err)
              
          })
  }, [])
  const submitHandler2 = (e) => {
        
    e.preventDefault()
    console.log("cool")
    const tempObj = {
      
        
    }
    console.log(tempObj)
    axios.delete(`http://localhost:5000/api/store/${store.id}`, tempObj)
        .then(res => {
            console.log("✅✅✅✅✅✅✅✅✅✅", res.data)
            alert (" the product is deletted")
            navigate('/')
           
        })
        .catch(err => {
            console.log("❌❌❌❌", err.response.data.errors.title.message)
            const errorResponse = err.response.data.errors; // Get the errors from err.response.data
            const errorArr = []; // Define a temp error array to push the messages in
            for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                errorArr.push(errorResponse[key].message)
            }
            // Set Errors
            setErrors(errorArr);
        })

}


  return (
    <div>AllStore
         <div class="text-lg-center">Store Finder</div>
         <div class="text-xl-start">Find Stores in your area !</div>
        <table class="table caption-top">
                 <thead>
                   <tr>
                     <th>Store</th>
                     <th>Store Number</th>
                       <th>Open</th>
                       <th>Remove</th>
                   </tr>
                 </thead>
                 { (store) ?
                    store.map((store) => {
                        return (
                       
                          <tbody>
                            <td>{store.Name}</td>
                            <td>{store.Number}</td>
                            <td>{store.Open ? "True" : "False"}</td>
                            <td> <button type="submit" class="btn btn-danger" onClick={submitHandler2}>dellet</button>                    </td>
                          </tbody>
                      
                        )
                    }) : <h1>Loading ...</h1>
            }

       
</table>
<Link to={"/stores/add" }>
                            <button>Can't fin your store?</button> 
                                </Link>
                                



    </div>
  )
}

export default AllStore
