import React, { useEffect, useState } from 'react'
import axios from "axios"
import { Routes, Route, Link } from "react-router-dom"

import Add from "./components/Add";
import Edit from "./components/Edit";
import Store from "./components/Store";
import AllStore from './components/AllStore';

function App() {
  const [store,setStore]= useState([])
  useEffect(() => {
    axios.get("http://localhost:5000/api/Store")
        .then(res => {
            console.log(res.data)
            setStore(res.data)


        })
        .catch(err => {
            console.log(err)

        })
}, [])







  return (
    <div className="container mt-4">
      <div>
      <div class="text-lg-center">Store Finder</div>
         <div class="text-xl-start">Find Stores in your area !</div>
        <table class="table caption-top">
                 <thead>
                   <tr>
                     <th>Store</th>
                     <th>Store Number</th>
                       <th>Open</th>
                       <th>Remove</th>
                   </tr>
                 </thead>
                 { (store) ?
                    store.map((store) => {
                        return (

                          <tbody>
                            <td>{store.Name}</td>
                            <td>{store.Number}</td>
                            <td>{store.Open ? "True" : "False"}</td>
                            <td>                          jhkhi </td>
                          </tbody>

                        )
                    }) : <h1>Loading ...</h1>
            }


</table>
<Link to={"/stores/add" }>
                                    <h3>open</h3>
                                </Link>







      </div>



        <Routes>
    <Route path="/stores/add" element={<Add/>}/>
    <Route path="/stores/edit/:id" element={<Edit/>}/>
    <Route path="/stores/:id" element={<Store/>}/>

        </Routes>


    </div>
  );
}

export default App;
